import Intro from './components/Intro';
import SignIn from './components/SignIn';
import Home from './components/Home';
import { BrowserRouter as Router, Route, Routes, Navigate, useParams, useNavigate } from 'react-router-dom';
import './App.css';
import { useState, useEffect } from 'react';
import { unstable_renderSubtreeIntoContainer } from 'react-dom';


function App() {

  const guestUser = {
    username: "guest",
    password: "guest",
  }

  const [user, setUser] = useState({username: "", password: ""})
  const [error, setError] = useState("");

  const navigate = useNavigate()




  const Login = details => {
      console.log(details);
      if(details.username == guestUser.username && details.password == guestUser.password) {
        console.log("Logged in as guest");
        setUser({
          username: details.username,    
        });
        navigate('/Home')



      } else {
        console.log("details do not match");
        setError("Details do not match.")

      }
  }

  const Logout = () => {
      console.log("logout");
  }

  useEffect(() => {
    const u = localStorage.getItem("user");
    console.log("useeffect" + u);
    console.log(u)

  }, [])


  useEffect(() => {
    localStorage.setItem("user", user)
  },[user])
  return (

    <div className="App">

      <Routes>    
        <Route path="/Login" element={<Intro Login={Login} Logout={Logout} error={error}/>} />
        <Route path="/Home" element={<Home user={user}/>}/>
        <Route path="" element={user.username !== "" ? <Navigate to ="/Home" /> :
          <Navigate to ="/Login"/>}/>

      </Routes>        
    </div>



  );
}

export default App;
