        password: p,
      })
      console.log("it worked")

    } 

  }, [])

  useEffect(() => {
    if (user.username == guestUser.username) {

      console.log("before save: " + user.username);
      localStorage.setItem("userUsername", user.username);
      localStorage.setItem("userPassword", user.password);
      let u = localStorage.getItem("userUsername");
      let p = localStorage.getItem("userPassword");
      console.log("Saved username: " + u);
      console.log("saved password: " + p);

    }



  },[user])

  */

  return (

    <div className="App">
      {LoginUser(users)}

      <Routes>    
        {(user.username == guestUser.username && user.password == guestUser.password) ? 
          <Route path="/Home" element={<><Header/><Home user={user}/></>}/> :
          <Route path="/Login" element={<Intro Login={Login} Logout={Logout} error={error}/>}/>}


        <Route path="" element={isAuth ? <Navigate to ="/Home" /> :
          <Navigate to ="/Login"/>}/>

      </Routes>        
    </div>



  );
}

export default App;
