import Buttons from './Buttons'
import SignUp from './SignUp'
import SignIn from './SignIn'
import { useState } from 'react'

const Intro = () => {

    const [showButtons, setShowButtons] = useState(true)

    const [showSignIn, setShowSignIn] = useState(false)


    const showSignInBtn = () => {
        if (!showSignIn) {
           setShowSignIn(true)
           setShowButtons(false)
        } else {
           setShowSignIn(false)
        }
     }

    const [showSignUp, setShowSignUp] = useState(false)
    const showSignUpBtn = () => {
        if (!showSignIn) {
           setShowSignUp(true)
           setShowButtons(false)
        } else {
           setShowSignUp(false)
        }
     }


    const backBtn = () => {
        if (showSignIn) {
            setShowSignIn(false)
            setShowButtons(true)

        } else if (showSignUp) {
            setShowSignUp(false)
            setShowButtons(true)
        }
    }

    const guestUser = {
        username: "Guest",
        password: guest,
    }

    const [user, setUser] = useState({name: "", email: ""})
