import Intro from './components/Intro';
import SignIn from './components/SignIn';
import Home from './components/Home';
import { BrowserRouter as Router, Route, Routes, Navigate, useParams, useNavigate } from 'react-router-dom';
import './App.css';
import { useState, useEffect } from 'react';
import { unstable_renderSubtreeIntoContainer } from 'react-dom';
import { type } from '@testing-library/user-event/dist/type';


function App() {

  const guestUser = {
    username: "guest",
    password: "guest",
  }

  const [user, setUser] = useState({username: "", password: ""})
  const [error, setError] = useState("");

  const navigate = useNavigate()




  const Login = details => {
      console.log(details);
      if(details.username == guestUser.username && details.password == guestUser.password) {
        console.log("Logged in as guest");
        setUser({
          username: details.username,   
          password: details.password 
        });
        navigate('/Home')



      } else {
        console.log("details do not match");
        setError("Details do not match.")

      }
  }

  const Logout = () => {
      console.log("logout");
  }



  useEffect(() => {
    let u = localStorage.getItem("userUsername");
    let p = localStorage.getItem("userPassword");
    let s = "a string";
    console.log("loaded username: " + u);
    console.log("loaded password: " + p);
    if (u == guestUser.username && p == guestUser.password) {
      setUser({
        username: u,
        password: p,
      })
      console.log("it worked")

    } 

  }, [])

  useEffect(() => {
    if (user.username == guestUser.username) {

      console.log("before save: " + user.username);
      localStorage.setItem("userUsername", user.username);
      localStorage.setItem("userPassword", user.password);
      let u = localStorage.getItem("userUsername");
      let p = localStorage.getItem("userPassword");
      console.log("Saved username: " + u);
      console.log("saved password: " + p);

    }



  },[user])



  return (

    <div className="App">

      <Routes>    
        {(user.username == guestUser.username && user.password == guestUser.password) ? 
          <Route path="/Home" element={<Home user={user}/>}/> :
          <Route path="/Login" element={<Intro Login={Login} Logout={Logout} error={error}/>}/>}


        <Route path="" element={user.username !== "" ? <Navigate to ="/Home" /> :
          <Navigate to ="/Login"/>}/>

      </Routes>        
    </div>



  );
}

export default App;
