      console.log("logout");
  }



  return (

    <div className="App">


      {(user.email != "") ? (<div className="welcome"><h2>Welcome, <span>{user.name}</span></h2>
        </div>
      ) : (
        <Intro Login={Login} Logout={Logout} error={error}/>
      )}

    </div>



  );
}

export default App;
