import Intro from './components/Intro';
import SignIn from './components/SignIn';
import Home from './components/Home';
import Header from './components/Header';
import { BrowserRouter as Router, Route, Routes, Navigate, useParams, useNavigate } from 'react-router-dom';
import './App.css';
import { useState, useEffect } from 'react';
import { unstable_renderSubtreeIntoContainer } from 'react-dom';
import { type } from '@testing-library/user-event/dist/type';
import 'bootstrap/dist/css/bootstrap.min.css';



function App() {


  let users = {
    "sebastian": "password",
    "henry": "hoover",
    "guest": "guest",
  }

  const [user, setUser] = useState({username: "", password: ""})
  const [error, setError] = useState("");
  const [test, setTest] =useState("");


  let usernameTaken = false
  let detailsNotMatch = false
  let usernameNotExist = false

  const navigate = useNavigate()
  const [isAuth, setIsAuth] = useState(false);

  const Login = details => {
    console.log(details);
    for (const key in users) {
      const value = users[key]

      if(details.username == key) {
        if (details.password == value) {
          setIsAuth(true);
          setUser({username: key, password: value});
          localStorage.setItem("userUsername", details.username);
          localStorage.setItem("userPassword", details.password);
          navigate('/home');
        } else {
          detailsNotMatch = true;
          console.log('not match');
        }

      } else {
        usernameNotExist = true;
        console.log('no exist');

      }

    }
    (detailsNotMatch && setError("Details do not match"));
    (usernameNotExist && setError("Username doesn't exist"));



  }

  const signUp = details => {
    console.log(details);

    console.log(details.username);
    console.log(details.password);
    let u = details.username;
    let p = details.password;
    let cp = details.confirmPassword;
    for (const key in users) {
      const value = users[key]
      if (details.username == key) {
        console.log("username taken");
        userTaken = true;

      } else {
        if (p == cp) {
          let obj = {};
          users[u] = p;
          console.log(users);





        } else {
          console.log("passwords don't match");
        }
      }
    }


  }

  const guestLogin = () => {
    setIsAuth(true);
    setUser({
      username: "guest",
      password: "guest",
    })
    localStorage.setItem("userUsername", "guest");
    localStorage.setItem("userPassword", "guest");
    authenticated = true;
    navigate('/home')
  }

  const Logout = () => {
    setIsAuth(false);
    setUser({
      username: "",
      password: "", 
    })
    localStorage.setItem("userUsername", "");
    localStorage.setItem("userPassword", "");
    authenticated = false;

    navigate('/Login');
  }


  let authenticated = false
  let userTaken = false

  useEffect(() => {
    const u = localStorage.getItem("userUsername");
    const p = localStorage.getItem("userPassword");
    for (const key in users) {
      const value = users[key]
      if (key == u && value == p) {
        user.username = u;
        user.password = p;
        authenticated = true;

      }

    }



    (authenticated && setIsAuth(true));

  }, []

  )


  return (

    <div className="App">

      {(error !== "") ? (<div className='error'>{error}</div>) : ""}
      <Routes>    
        {isAuth ? 
          <Route path="/Home" element={<><Header Logout={Logout}/><Home user={user}/></>}/> :
          <Route path="/Login" element={<Intro Login={Login} guestLogin={guestLogin} signUp={signUp}/>}/>}


        <Route path="" element={isAuth ? <Navigate to ="/Home" /> :
          <Navigate to ="/Login"/>}/>

      </Routes>        
    </div>



  );
}

export default App;
