import React from 'react'
import Buttons from './Buttons'
import SignUp from './SignUp'
import SignIn from './SignIn'
import { useState } from 'react'

const Intro = ({Login, Logout, error}) => {


    const [showButtons, setShowButtons] = useState(true)

    const [showSignIn, setShowSignIn] = useState(false)


    const showSignInBtn = () => {
        if (!showSignIn) {
           setShowSignIn(true)
           setShowButtons(false)
        } else {
           setShowSignIn(false)
        }
     }

    const [showSignUp, setShowSignUp] = useState(false)
    const showSignUpBtn = () => {
        if (!showSignIn) {
           setShowSignUp(true)
           setShowButtons(false)
        } else {
           setShowSignUp(false)
        }
     }


    const backBtn = () => {
        if (showSignIn) {
            setShowSignIn(false)
            setShowButtons(true)

        } else if (showSignUp) {
            setShowSignUp(false)
            setShowButtons(true)
        }
    }

    const [details, setDetails] = useState({name: "", password: ""});

    const submitHandler = e => {
        e.preventDefault();
        Login(details);
    }






    return (
        <div className='introBox'>
            <div className='titleMicroReactor'>
                CARDIFF MICROREACTOR
            </div>
            {showButtons && <Buttons showSignInBtn={showSignInBtn} showSignUpBtn={showSignUpBtn}/>}
            {showSignIn && <SignIn backBtn={backBtn} submitHandler={submitHandler} Login={Login}/>}
            {showSignUp && <SignUp backBtn={backBtn}/>}



        </div>
    )
}

export default Intro
